<template>
    <div 
        class="image_component"
        ref="root"
        @click="on_click"
        @mouseover="on_pointerover"
        @mouseout="on_pointerout"
    >
        <img
                ref="image_element"
                v-bind:class="{
                        loaded: this.image_loaded
                }"
                :src="image_src"
                @load="on_image_loaded"
        />
    </div>
</template>



<script lang="ts">
import Vue from 'vue';
import { C } from "./Include"

export default Vue.extend({
  name: 'ImageCompnent',

  components: {

  },

  props: {
          image_src: {
                  type: String,
                  default: ()=>""
          }
  },

  data () {
          return {
                real_width: 0,
                real_height: 0,
                image_loaded: false
          }
  },

  methods: {
          on_image_loaded ( evt: Event ) {
                this.real_width = this.$refs.image_element.width
                this.real_height = this.$refs.image_element.height
                this.image_loaded = true
          },
          on_click () {
                  this.$emit("click")
          },
          on_pointerover () {
                this.$emit("pointerover")
          },
          on_pointerout () {
                  this.$emit("pointerout")
          }
  }
});
</script>


<style scoped lang="sass">
    .image_component 
        img 
                width: auto
                height: 100%
                opacity: 0
                transition: opacity 1s ease

                &.loaded 
                        opacity: 1
        

</style>